import React, { Component } from 'react'
import MemberService from '../service/MemberService';


export class GetMemberById extends Component {
    constructor(props){
        super(props);
        this.state = {
            members: []
        }

    this.memberId=this.memberId.bind(this);

    }
    componentDidMount(){
        
        MemberService.getAllMembers().then ((res) =>{
            console.log(res.data);
            this.setState({members : res.data});
        })
    }




    changeMemberNameHandler(event){
        this.setState({memberName:event.target.value})
    }
    changeMemberPassHandler(event){
        this.setState({memberPass:event.target.value})
    }
    changeMemberEmailHandler(event){
        this.setState({memberEmail:event.target.value})
    }
    changeMemberContactHandler(event){
        this.setState({memberContact:event.target.value})
    }
    changeMemberAddressHandler(event){
        this.setState({memberAddress:event.target.value})
    }
    changeMemberDOBHandler(event){
        this.setState({memberDOB:event.target.value})
    }
    





    render() {

        
        return (

            <div className='getmemberbyid'>
                <h1>Member Information</h1>
                <div style={{margin:'50px'}}>
                    <div className='now' style={{ display: 'flex',flexWrap: 'wrap'}}>
                        {
                            this.state.members.map(member => {
                                return <member Key={member.memberId} dif={member.memberId} Name={member.memberName} Email={member.memberEmail} Contact={member.memberContact} Address={member.memberAddress} DOB={member.memberDOB} Password={member.memberPass}/>
                            })
                        }
                    </div>
                </div>
                {/* <button onClick={this.addingTry}>Add new Food</button>

                <button style={{marginLeft:'30px'}}onClick={this.visitToCart}>Go to cart</button> */}
            </div>
        )

    }

}
