import React from "react"
import { Link } from "react-router-dom";
import '../css/MemberLogin.css';
export class MemberLogin extends React.Component{  
  constructor(props){
    super(props);
    this.state ={
      userid:''
    }
    this.changeIdHandler = this.changeIdHandler.bind(this);
}

changeIdHandler(event){
    this.setState({userid:event.target.value})
}
    render(){
        return(
          
      <div className="Member">

      <form className="Auth-form">
        <div className="Auth-form-content">
          <h3 className="Auth-form-title">Sign In</h3>
          <div className="form-group">
                                  <label>Enter Name</label>
                                  <input type="text" name="userid" id="" className="form-control" placeholder="Name" 
                                  value={this.state.userid} onChange = {this.changeIdHandler}/>
                                </div>
          <div className="form-group mt-3">
            <label>Password</label>
            <input
              type="password"
              className="form-control mt-1"
              placeholder="Enter password"
            />
          </div>
          <div className="d-grid gap-2 mt-3">
          <Link to={"/getmember/"+this.state.userid} className="btn btn-success">
              Submit
          </Link>
          </div>
          <p className="forgot-password text-right mt-2">
            Forgot <a href="#">password?</a>
          </p>
        </div>
      </form>
      <div>
                   <footer className="style">
                    Â© 2022 Claim Management System
                    </footer>
                    </div>
    </div>
    
  )
}
}
